import React from "react";
import {
  Input,
  Label,
  InputGroup,
  ErrorText,
  ValidationIcon
} from "../elements/Forms";
import {
  faCheckCircle,
  faTimesCircle
} from "@fortawesome/free-solid-svg-icons";

const InputComponent = ({
  state,
  changeState,
  type,
  label,
  placeholder,
  name,
  errorText,
  regularExpression,
  Inputfunction,
  obligatory
}) => {

  const convertToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const fileReader = new FileReader();
      fileReader.readAsDataURL(file);
      fileReader.onload = () => {
        resolve(fileReader.result);
      };
      fileReader.onerror = (error) => {
        reject(error);
      };
    });
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    const base64 = await convertToBase64(file);
  };

  const onChange = (e) => {
    changeState({ ...state, field: e.target.value });

    if (name === "avatar") {
      handleFileUpload(e.target.files[0]);
    }
  };  

  const validation = () => {
    if (regularExpression) {
      if (regularExpression.test(state.field)) {
        changeState({ ...state, valid: "true" });
      } else {
        changeState({ ...state, valid: "false" });
      }
    }

    if (Inputfunction) {
      Inputfunction();
    }
  };

  return (
    <div>
      <Label htmlFor={name} valid={state.valid}>
        {label}
        {obligatory === "true" && <abbr title="requiered">*</abbr>}
      </Label>
      <InputGroup>
        <Input
          type={type}
          placeholder={placeholder}
          id={name}
          value={state.field}
          onChange={onChange}
          onKeyUp={validation}
          onBlur={validation}
          valid={state.valid}
          obligatory={obligatory}
        />
        <ValidationIcon
          icon={state.valid === "true" ? faCheckCircle : faTimesCircle}
          valid={state.valid}
        />
      </InputGroup>
      <ErrorText valid={state.valid}>{errorText}</ErrorText>
    </div>
  );
};

export default InputComponent;
