import React from "react";
import {
  Input,
  Label,
  InputGroup,
  ErrorText
} from "../elements/Forms";

const InputFileComponent = ({
  state,
  changeState,
  type,
  label,
  name,
  errorText,
  obligatory
}) => {

	const onChange = (e) => {
		changeState(previousInputs => ({ ...previousInputs, field: e.target.value}))
	};	

  function convertBase64(file){
		return new Promise((resolve, reject) => {
			const fileReader = new FileReader();
			fileReader.readAsDataURL(file)
			fileReader.onload = () => {
				resolve(fileReader.result);
			}
			fileReader.onerror = (error) => {
				reject(error);
			}
		})
	};

  const handleFileRead = async(event) => {
		const file = event.target.files[0]
		var base64 = await convertBase64(file)
    changeState(previousInputs => ({ ...previousInputs, myFile: base64 }))
		console.log(base64)
  };

  return (
    <div>
      <Label htmlFor={name} valid={state.valid}>
        {label}
      </Label>
      <InputGroup>
        <Input
          type={type}
          id={name}
					accept
          value={state.field}
          onChange={onChange}
					onSubmit={handleFileRead}
          obligatory={obligatory}
        />
      </InputGroup>
      <ErrorText valid={state.valid}>{errorText}</ErrorText>
    </div>
  );
};

export default InputFileComponent;
